import React from 'react'
import Chart from 'chart.js'

const LineChart = () => {
  const ctx = 'myChart'

  const myChart = new Chart(ctx, {
    tipe: 'line',
    data: {
      labels: ['Red', 'Blue', 'Yellow'],
      datasets: [{
        label: '# of Votes',
        data: [12, 19, 3],
        backgroundColor: [
          'rgba(255, 99, 132, .2)',
          'rgba(54, 162, 235, .2)',
          'rgba(255, 206, 86, .2)',
        ],
        borderColor: [
          'rgba(255, 99, 132, .2)',
          'rgba(54, 162, 235, .2)',
          'rgba(255, 206, 86, .2)',
        ],
        borderWidth: 1
      }]
    },
    options: {
      color: ['#70C0D0'],
      responsive: true,
      legend: {
        display: false,
      },  
      title: {
        display: false,
        text: 'Título',
        fontColor: '#FFFFFF'
      },
      tooltips: {
        mode: 'index',
        intersect: false,
    },
    hover: {
        mode: 'nearest',
        intersect: true
    },
    scales: {
        yAxes: [{
            ticks: {
                fontColor: "white",
                beginAtZero: true
            },
            gridLines: {
                zeroLineColor: '#000000',
                color: '#606163',
            }
        }],
        xAxes: [{
            ticks: {
                fontColor: "white",
                beginAtZero: true
            },
            gridLines: {
                zeroLineColor: '#000000',
                color: '#606163',
            }
        }]
    }

    }
  })

  return (
    <canvar id="myChart" width="400" height="400" />
  )
}

export default LineChart
