import React, {useRef, useEffect, useState} from "react";
import Chart from 'chart.js';

const LineChart = ({labels, title, zeroLineColor}) => {
    const data = {

            label: 'Mensagens',
            data: ['1', '32', '3', '4'],
            backgroundColor: '#0ce',
            borderColor: '#0ce',
            fill: false,

    }

    const canvas = useRef(null);

    const [stateChart, setStateChart] = useState(null);

    componentDidMount(() => {

        if (stateChart !== null) stateChart.destroy();

        const lineChart = new Chart(canvas.current, {
            type: 'line',
            data: {
                labels: ['tit1', 'tit2', 'tit1', 'tit2'],
                datasets: data,
            },
            options: {
                color: ['#70CAD1'],
                responsive: true,
                legend: {
                    display: false,
                },
                title: {
                    display: false,
                    text: 'Mensgaem',
                    fontColor: '#FFFFFF',
                },
                tooltips: {
                    mode: 'index',
                    intersect: false,
                },
                hover: {
                    mode: 'nearest',
                    intersect: true
                },
                scales: {
                    yAxes: [{
                        ticks: {
                            fontColor: "white",
                            beginAtZero: true
                        },
                        gridLines: {
                            zeroLineColor: '#012',
                            color: '#606163',
                        }
                    }],
                    xAxes: [{
                        ticks: {
                            fontColor: "white",
                            beginAtZero: true
                        },
                        gridLines: {
                            zeroLineColor: '#012',
                            color: '#606163',
                        }
                    }]
                }
            },
        });

        setStateChart(lineChart);
    })

    return (
        <canvas ref={canvas} />
    );
};

export default LineChart;
